import{_ as s,c as a,o as n,a as e}from"./app.d5b54cdf.js";const h=JSON.parse('{"title":"Ruby on rails \u4E4B\u521B\u5EFA\u9879\u76EE","description":"","frontmatter":{},"headers":[{"level":2,"title":"RubyGems","slug":"rubygems"},{"level":2,"title":"Bundler","slug":"bundler"},{"level":2,"title":"\u5B89\u88C5 rails","slug":"\u5B89\u88C5-rails"},{"level":2,"title":"\u5B89\u88C5\u6570\u636E\u5E93\u9A71\u52A8\u5305","slug":"\u5B89\u88C5\u6570\u636E\u5E93\u9A71\u52A8\u5305"},{"level":2,"title":"\u521B\u5EFA rails \u9879\u76EE","slug":"\u521B\u5EFA-rails-\u9879\u76EE"},{"level":2,"title":"\u542F\u52A8\u6570\u636E\u5E93\u5BB9\u5668","slug":"\u542F\u52A8\u6570\u636E\u5E93\u5BB9\u5668"},{"level":2,"title":"\u8FDE\u63A5\u6570\u636E\u5E93","slug":"\u8FDE\u63A5\u6570\u636E\u5E93"},{"level":2,"title":"\u542F\u52A8 rails \u9879\u76EE","slug":"\u542F\u52A8-rails-\u9879\u76EE"}],"relativePath":"blog/ruby/ruby_on_rails_01_create/index.md"}'),l={name:"blog/ruby/ruby_on_rails_01_create/index.md"},p=e(`<h1 id="ruby-on-rails-\u4E4B\u521B\u5EFA\u9879\u76EE" tabindex="-1">Ruby on rails \u4E4B\u521B\u5EFA\u9879\u76EE <a class="header-anchor" href="#ruby-on-rails-\u4E4B\u521B\u5EFA\u9879\u76EE" aria-hidden="true">#</a></h1><h2 id="rubygems" tabindex="-1">RubyGems <a class="header-anchor" href="#rubygems" aria-hidden="true">#</a></h2><p>Ruby \u7684\u5305\uFF08gem\uFF09\u7BA1\u7406\u5DE5\u5177\u3002</p><p>\u7C7B\u4F3C\u4E8E NPM\uFF0C\u4F46\u4E3B\u8981\u7528\u4E8E\u7BA1\u7406\u5168\u5C40\u7684 gems\u3002</p><p>\u67E5\u770B\u5F53\u524D\u4E0B\u8F7D\u6E90\uFF1A</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">gem sources -l</span></span>
<span class="line"></span></code></pre></div><p>\u5982\u679C\u6E90\u4E0D\u662F ruby-china \u5EFA\u8BAE\u5207\u6362\uFF1A</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">gem sources --add https://gems.ruby-china.com/ --remove https://rubygems.org/</span></span>
<span class="line"></span></code></pre></div><h2 id="bundler" tabindex="-1">Bundler <a class="header-anchor" href="#bundler" aria-hidden="true">#</a></h2><p>\u4E5F\u662F Ruby \u7684\u5305\uFF08gem\uFF09\u7BA1\u7406\u5DE5\u5177\u3002</p><p>\u548C RubyGems \u4E0D\u540C\u7684\u662F\uFF0C\u5B83\u4E3B\u8981\u7528\u4E8E\u7BA1\u7406\u9879\u76EE\u5C40\u90E8\u7684 gems\uFF0C\u80FD\u591F\u8DDF\u8E2A\u5E76\u5B89\u88C5\u6240\u9700\u7684\u7279\u5B9A\u7248\u672C\u7684 gem\uFF0C\u4EE5\u6B64\u6765\u4E3A Ruby \u9879\u76EE\u63D0\u4F9B\u4E00\u81F4\u7684\u8FD0\u884C\u73AF\u5883\u3002</p><p>\u5207\u6362\u81F3\u56FD\u5185\u6E90\uFF1A</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">bundle config mirror.https://rubygems.org https://gems.ruby-china.com</span></span>
<span class="line"></span></code></pre></div><p>\u4E5F\u53EF\u4EE5\u4FEE\u6539 Gemfile \u7684 source\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">source &#39;https://gems.ruby-china.com&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="\u5B89\u88C5-rails" tabindex="-1">\u5B89\u88C5 rails <a class="header-anchor" href="#\u5B89\u88C5-rails" aria-hidden="true">#</a></h2><p>\u8FD9\u91CC\u4F7F\u7528 gem \u5168\u5C40\u5B89\u88C5 rails \u5E76\u6307\u5B9A\u4E86\u7248\u672C\uFF1A</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">gem install rails -v 7.0.2.3</span></span>
<span class="line"></span></code></pre></div><h2 id="\u5B89\u88C5\u6570\u636E\u5E93\u9A71\u52A8\u5305" tabindex="-1">\u5B89\u88C5\u6570\u636E\u5E93\u9A71\u52A8\u5305 <a class="header-anchor" href="#\u5B89\u88C5\u6570\u636E\u5E93\u9A71\u52A8\u5305" aria-hidden="true">#</a></h2><p>\u7531\u4E8E\u6570\u636E\u5E93\u7528\u7684\u662F postgresql\uFF0C\u6240\u4EE5\u8FD9\u91CC\u5B89\u88C5\u5BF9\u5E94\u7684\u9A71\u52A8\u5305\uFF1A</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">pacman -S postgresql-libs</span></span>
<span class="line"></span></code></pre></div><p><code>pacman</code>\u662F<code>Arch Linux</code>\u7528\u4E8E\u5B89\u88C5\u8F6F\u4EF6\u7684\u5DE5\u5177\u3002</p><h2 id="\u521B\u5EFA-rails-\u9879\u76EE" tabindex="-1">\u521B\u5EFA rails \u9879\u76EE <a class="header-anchor" href="#\u521B\u5EFA-rails-\u9879\u76EE" aria-hidden="true">#</a></h2><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">rails new --api --database=postgresql --skip-test mangosteen-1</span></span>
<span class="line"></span></code></pre></div><ul><li><code>--api</code>\u53EA\u4F7F\u7528 api \u6A21\u5F0F\u3002</li><li><code>--database=postgresql</code>\u4F7F\u7528 postgresql \u4F5C\u4E3A\u6570\u636E\u5E93\u3002</li><li><code>--skip-test</code>\u8DF3\u8FC7\u6D4B\u8BD5\uFF0C\u540E\u7EED\u53EF\u4EE5\u4F7F\u7528\u7B2C\u4E09\u65B9\u6D4B\u8BD5\u5DE5\u5177\u3002</li><li><code>mangosteen-1</code>\u9879\u76EE\u540D\u79F0\u5E76\u521B\u5EFA\u5BF9\u5E94\u7684\u76EE\u5F55\u3002</li></ul><h2 id="\u542F\u52A8\u6570\u636E\u5E93\u5BB9\u5668" tabindex="-1">\u542F\u52A8\u6570\u636E\u5E93\u5BB9\u5668 <a class="header-anchor" href="#\u542F\u52A8\u6570\u636E\u5E93\u5BB9\u5668" aria-hidden="true">#</a></h2><p><strong>\u6CE8\u610F</strong>\u662F\u989D\u5916\u542F\u52A8\u4E00\u4E2A\u5BB9\u5668\u670D\u52A1\uFF0C\u800C\u4E0D\u662F\u5728\u5DF2\u6709\u5BB9\u5668\u73AF\u5883\u4E2D\u6267\u884C\u3002</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">docker run -d \\</span></span>
<span class="line"><span style="color:#A6ACCD;">    --name db-for-mangosteen \\</span></span>
<span class="line"><span style="color:#A6ACCD;">    -e POSTGRES_USER=mangosteen \\</span></span>
<span class="line"><span style="color:#A6ACCD;">    -e POSTGRES_PASSWORD=123456 \\</span></span>
<span class="line"><span style="color:#A6ACCD;">    -e POSTGRES_DB=mangosteen_dev \\</span></span>
<span class="line"><span style="color:#A6ACCD;">    -e PGDATA=/var/lib/postgresql/data/pgdata \\</span></span>
<span class="line"><span style="color:#A6ACCD;">    -v mangosteen-data:/var/lib/postgresql/data \\</span></span>
<span class="line"><span style="color:#A6ACCD;">    --network=network1 \\</span></span>
<span class="line"><span style="color:#A6ACCD;">    postgres:14</span></span>
<span class="line"></span></code></pre></div><ul><li><code>-d</code>\u5728\u540E\u53F0\u6301\u7EED\u8FD0\u884C\u5BB9\u5668\u3002</li><li><code>--name db-for-mangosteen</code>\u5BB9\u5668\u7684\u540D\u79F0\u3002</li><li><code>-e</code>\u5199\u5165\u73AF\u5883\u53D8\u91CF\u3002</li><li><code>-v mangosteen-data:/var/lib/postgresql/data</code>\u521B\u5EFA\u6570\u636E\u5377\u5E76\u6620\u5C04\uFF0C\u7528\u4E8E\u6570\u636E\u6301\u4E45\u5316\u3002</li><li><code>--network=network1</code>\u6307\u5B9A\u8981\u52A0\u5165\u7684\u7F51\u7EDC\u3002</li><li><code>postgres:14</code>\u6307\u5B9A\u4F7F\u7528\u7684\u6570\u636E\u5E93\u5BB9\u5668\u955C\u50CF\u7248\u672C\u3002</li></ul><p>\u5982\u679C\u62A5\u9519\u63D0\u793A\u627E\u4E0D\u5230\u7F51\u7EDC\uFF0C\u5219\u9700\u8981\u5148\u521B\u5EFA\u5BF9\u5E94\u7684\u7F51\u7EDC\uFF1A</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">docker network create network1</span></span>
<span class="line"></span></code></pre></div><h2 id="\u8FDE\u63A5\u6570\u636E\u5E93" tabindex="-1">\u8FDE\u63A5\u6570\u636E\u5E93 <a class="header-anchor" href="#\u8FDE\u63A5\u6570\u636E\u5E93" aria-hidden="true">#</a></h2><p>\u8FDB\u5165 rails \u9879\u76EE\u76EE\u5F55 mangosteen-1\uFF0C\u4FEE\u6539<code>config/database.yml</code>\u6587\u4EF6\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">development:</span></span>
<span class="line"><span style="color:#A6ACCD;">  &lt;&lt;: *default</span></span>
<span class="line"><span style="color:#A6ACCD;">  database: mangosteen_dev</span></span>
<span class="line"><span style="color:#A6ACCD;">  username: mangosteen</span></span>
<span class="line"><span style="color:#A6ACCD;">  password: 123456</span></span>
<span class="line"><span style="color:#A6ACCD;">  host: db-for-mangosteen</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="\u542F\u52A8-rails-\u9879\u76EE" tabindex="-1">\u542F\u52A8 rails \u9879\u76EE <a class="header-anchor" href="#\u542F\u52A8-rails-\u9879\u76EE" aria-hidden="true">#</a></h2><p>\u8FD9\u91CC\u63A8\u8350\u4F18\u5148\u4F7F\u7528\u5C40\u90E8\u7684 rails \u7248\u672C\u542F\u52A8\uFF1A</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">bundle exe rails server</span></span>
<span class="line"><span style="color:#676E95;"># \u7B49\u4EF7\u4E8E</span></span>
<span class="line"><span style="color:#A6ACCD;">bin/rails server</span></span>
<span class="line"></span></code></pre></div><p>\u4E5F\u53EF\u4EE5\u4F7F\u7528\u5168\u5C40\u7684 rails \u7248\u672C\u542F\u52A8\uFF1A</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">rails server</span></span>
<span class="line"></span></code></pre></div><p>\u4F46\u662F\u6709\u53EF\u80FD\u548C\u5C40\u90E8\u7684\u7248\u672C\u4E0D\u4E00\u81F4\uFF0C\u6240\u4EE5\u4E0D\u63A8\u8350\u3002</p>`,40),o=[p];function c(r,t,i,d,u,b){return n(),a("div",null,o)}const y=s(l,[["render",c]]);export{h as __pageData,y as default};
